import React, { useContext, useState, useEffect } from "react";
import axios from 'axios';

const BASE_URL = "http://localhost:5000/api/v1/";

const GlobalContext = React.createContext();

export const GlobalProvider = ({ children }) => {
    const [incomes, setIncomes] = useState([]);
    const [expenses, setExpenses] = useState([]);
    const [error, setError] = useState(null);

    // Vorhandene Funktion: Abrufen der Einkommen
    const getIncomes = async () => {
        try {
            const response = await axios.get(`${BASE_URL}get-incomes`);
            setIncomes(response.data);
        } catch (err) {
            setError(err.response?.data?.message || "Ein Fehler ist aufgetreten");
        }
    };

    // Vorhandene Funktion: Hinzufügen eines Einkommens
    const addIncome = async (income) => {
        try {
            await axios.post(`${BASE_URL}add-income`, income);
            getIncomes();
        } catch (err) {
            setError(err.response?.data?.message || "Ein Fehler ist aufgetreten");
        }
    };

    // Vorhandene Funktion: Löschen eines Einkommens
    const deleteIncome = async (id) => {
        try {
            await axios.delete(`${BASE_URL}delete-income/${id}`);
            getIncomes();
        } catch (err) {
            setError(err.response?.data?.message || "Ein Fehler ist aufgetreten");
        }
    };

    // Vorhandene Funktion: Gesamteinkommen berechnen
    const totalIncome = () => {
        return incomes.reduce((acc, income) => acc + income.amount, 0);
    };

    // NEU: Wiederholen eines Einkommens
    const repeatIncome = async (id) => {
        try {
            const incomeToRepeat = incomes.find(income => income._id === id);
            if (!incomeToRepeat) {
                setError("Einkommen nicht gefunden");
                return;
            }

            const newIncome = {
                ...incomeToRepeat,
                _id: String(Date.now()), // Neue ID generieren
                date: new Date() // Optional: aktuelles Datum setzen
            };

            await axios.post(`${BASE_URL}add-income`, newIncome);
            getIncomes();
        } catch (err) {
            setError(err.response?.data?.message || "Ein Fehler ist aufgetreten");
        }
    };

    return (
        <GlobalContext.Provider value={{
            incomes,
            expenses,
            error,
            getIncomes,
            addIncome,
            deleteIncome,
            totalIncome,
            repeatIncome, // Die neue Funktion wird exportiert
        }}>
            {children}
        </GlobalContext.Provider>
    );
};

export const useGlobalContext = () => useContext(GlobalContext);
