import React from 'react';
import {
    Chart as ChartJs,
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
} from 'chart.js';

import { Line } from 'react-chartjs-2';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import { dateFormat } from '../../utils/dateFormat';

ChartJs.register(
    CategoryScale,
    LinearScale,
    PointElement,
    LineElement,
    Title,
    Tooltip,
    Legend,
    ArcElement,
);

function Chart() {
    const { incomes, expenses } = useGlobalContext();

    // Kombiniere alle Datumswerte aus incomes und expenses und sortiere sie
    const allDates = [...incomes, ...expenses]
        .map(entry => entry.date)
        .sort((a, b) => new Date(a) - new Date(b));

    // Entferne doppelte Datumswerte
    const uniqueDates = Array.from(new Set(allDates)).map(date => dateFormat(date));

    // Mappe die Income-Daten auf die entsprechende Position
    const incomeData = uniqueDates.map(date => {
        const incomeEntry = incomes.find(inc => dateFormat(inc.date) === date);
        return incomeEntry ? incomeEntry.amount : 0;
    });

    // Mappe die Expense-Daten auf die entsprechende Position
    const expenseData = uniqueDates.map(date => {
        const expenseEntry = expenses.find(exp => dateFormat(exp.date) === date);
        return expenseEntry ? expenseEntry.amount : 0;
    });

    const data = {
        // Labels für die x-Achse basierend auf allen einzigartigen Datumswerten
        labels: uniqueDates,
        datasets: [
            {
                label: 'Income',
                data: incomeData,
                backgroundColor: 'green',
                borderColor: 'green',
                tension: 0.3,
                fill: false,
            },
            {
                label: 'Expenses',
                data: expenseData,
                backgroundColor: 'red',
                borderColor: 'red',
                tension: 0.3,
                fill: false,
            }
        ]
    };

    const options = {
        scales: {
            x: {
                type: 'category', // Standard x-Achse, um konsistente Kategorien darzustellen
                title: {
                    display: true,
                    text: 'Date',
                },
            },
            y: {
                title: {
                    display: true,
                    text: 'Amount',
                },
            },
        },
    };

    return (
        <ChartStyled>
            <Line data={data} options={options} />
        </ChartStyled>
    );
}

const ChartStyled = styled.div`
    background: #FCF6F9;
    border: 2px solid #FFFFFF;
    box-shadow: 0px 1px 15px rgba(0, 0, 0, 0.06);
    padding: 1rem;
    border-radius: 20px;
    height: 100%;
`;

export default Chart;
